//-----------------------------------------------------------------
// Clocked Block for machine csm
//-----------------------------------------------------------------
always @(
   posedge clk
) 
begin : clocked_block_proc
   if (!rst) begin
      current_state <= reset;
      csm_timer <= 8'd0;
   end
   else if (timeoutrst) begin
      current_state <= reset;
      csm_timer <= 8'd0;
   end
   else if (abort) begin
      current_state <= reset;
      csm_timer <= 8'd0;
   end
   else 
   begin
      current_state <= next_state;
      csm_timer <= csm_next_timer;
   end
end // Clocked Block

//-----------------------------------------------------------------
// Wait state logic for machine csm
//-----------------------------------------------------------------
always @(
   csm_timer,
   csm_to_waitCAN,
   csm_to_end_readst,
   csm_to_ST_Start_Trim,
   csm_to_Wait
)
begin : csm_wait_block_proc
   csm_timeout = (csm_timer == 8'd0);
   if (csm_to_waitCAN == 1'b1) begin
      csm_next_timer = 8'd149;  //no cycles(150)-1=149
   end
   else if (csm_to_end_readst == 1'b1) begin
      csm_next_timer = 8'd3;  //no cycles(4)-1=3
   end
   else if (csm_to_ST_Start_Trim == 1'b1) begin
      csm_next_timer = 8'd59;  //no cycles(60)-1=59
   end
   else if (csm_to_Wait == 1'b1) begin
      csm_next_timer = 8'd239;  //no cycles(240)-1=239
   end
   else begin
      csm_next_timer = (csm_timeout)? 8'd0: (csm_timer - 8'd1);
   end
end // Wait State Block
