`resetall
`timescale 1ns/10ps
module data_generator_SM( 
   // Port Declarations
   // Port Declarations
   // Port Declarations
   // Port Declarations
   // Port Declarations
   // Port Declarations
   // Port Declarations
   input   wire           clk,               // write FIFO clk
   input   wire           rst,               // should be used through VIO
   output  reg            done,              // dbg
   input   wire           end_send_msg, 
   output  reg            irq_elink,         //  ena signal
   output  reg            counter_en, 
   output  reg            buffer_en, 
   output  reg     [2:0]  state_o, 
   input   wire           loop_en, 
   output  reg            end_read_elink, 
   input   wire           cnt_done, 
   input   wire           sign_on_sig, 
   input   wire           start_read_elink
);


// Internal Declarations


// Module Declarations

// State encoding
parameter 
          ST_Done_Wait  = 3'd0,
          ST_DONE       = 3'd1,
          ST_RESET      = 3'd2,
          ST_irq        = 3'd3,
          ST_Select_Sig = 3'd4,
          ST_en_buffer  = 3'd5;

reg [2:0] current_state, next_state;

//-----------------------------------------------------------------
// Next State Block for machine csm
//-----------------------------------------------------------------
always @(
   cnt_done, 
   current_state, 
   end_send_msg, 
   loop_en, 
   sign_on_sig, 
   start_read_elink
)
begin : next_state_block_proc
   case (current_state) 
      ST_Done_Wait: begin
         if (cnt_done ==1)
            next_state = ST_DONE;
         else
            next_state = ST_irq;
      end
      ST_DONE: begin
         if (loop_en == 1)
            next_state = ST_RESET;
         else
            next_state = ST_DONE;
      end
      ST_RESET: begin
         if (sign_on_sig == 1)
            next_state = ST_irq;
         else
            next_state = ST_RESET;
      end
      ST_irq: begin
         if (start_read_elink == 1)
            next_state = ST_Select_Sig;
         else
            next_state = ST_irq;
      end
      ST_Select_Sig: begin
         next_state = ST_en_buffer;
      end
      ST_en_buffer: begin
         if (end_send_msg == 1)
            next_state = ST_Done_Wait;
         else
            next_state = ST_en_buffer;
      end
      default: 
         next_state = ST_RESET;
   endcase
end // Next State Block

//-----------------------------------------------------------------
// Output Block for machine csm
//-----------------------------------------------------------------
always @(
   current_state
)
begin : output_block_proc
   // Default Assignment
   done = 0;
   irq_elink = 0;
   counter_en = 0;
   buffer_en = 0;
   end_read_elink = 0;

   // Combined Actions
   case (current_state) 
      ST_Done_Wait: begin
         //rest the wait 
         state_o     = 3'b101;
      end
      ST_DONE: begin
         state_o = 3'b110;
         done =1;
      end
      ST_RESET: begin
         state_o     = 3'b000;
         done = 0;
         irq_elink = 0;
         counter_en = 0;
         buffer_en = 0;
         end_read_elink = 0;
      end
      ST_irq: begin
         state_o     = 3'b010;
         irq_elink      = 1;
      end
      ST_Select_Sig: begin
         state_o     = 3'b011;
         counter_en = 1;
         end_read_elink = 1;
      end
      ST_en_buffer: begin
         state_o     = 3'b100;
         counter_en = 0;
         buffer_en = 1;
      end
   endcase
end // Output Block

//-----------------------------------------------------------------
// Clocked Block for machine csm
//-----------------------------------------------------------------
always @(
   posedge clk, 
   negedge rst
) 
begin : clocked_block_proc
   if (!rst) begin
      current_state <= ST_RESET;
   end
   else 
   begin
      current_state <= next_state;
   end
end // Clocked Block

endmodule // data_generator_SM
