                delimeter_r  <= 2'b00;
                end
        2'b10 : begin 
                 dout_i  <= 16'hBEEF;
                 delimeter_r <= 2'b00;
                end
        2'b11 : begin 
                 dout_i  <=16'h01;
                 delimeter_r  <=2'b01;
                end
    endcase
  else
  begin
    dout_i <= 16'h0;
    delimeter_r  <=2'b11;
