begin : csm_wait_block_proc
   csm_timeout = (csm_timer == 3'd0);
   if (csm_to_ST_wait_buffer1 == 1'b1) begin
      csm_next_timer = 3'd3;  //no cycles(4)-1=3
   end
   else begin
      csm_next_timer = (csm_timeout)? 3'd0: (csm_timer - 3'd1);
   end
