      current_state <= ST_reset;
   end
   else if (timeoutrst) begin
      current_state <= ST_reset;
   end
   else if (abort) begin
      current_state <= ST_endwait;
   end
   else if (endwait) begin
      current_state <= ST_reset;
   end
   else 
   begin
      current_state <= next_state;
   end
end // Clocked Block

