  // Counter over the select signal
  always @(posedge clk)
  begin
    if (!rst)
    send_count <= 3'b0;
    else
    begin
      if (send_out_trig == 1)
      send_count <= 3'b0;
      else  
      send_count <= send_count + 1;
    end
  end                                   


