//-----------------------------------------------------------------
always @(
   posedge clk, 
   negedge reset, 
   negedge restirq
) 
begin : clocked_block_proc
   if (!reset) begin
      current_state <= endwaitst;
   end
   else if (!restirq) begin
      current_state <= rst_rec_irq_can1;
   end
   else 
   begin
      if (endwait) begin
         current_state <= resetloop;
      end
      else 
      begin
         current_state <= next_state;

         // Combined Actions
         case (current_state) 
            resetbus: begin
               can_rec_reg = can_rec;
               can_rec_counter = 32'b1;//reset the counter
            end
            donebus0: begin
