//-----------------------------------------------------------------
// Clocked Block for machine csm
//-----------------------------------------------------------------
always @(
   posedge clk, 
   negedge rst
) 
begin : clocked_block_proc
   if (!rst) begin
      current_state <= reset;
      csm_timer <= 9'd0;
   end
   else 
   begin
      if (timeoutrst) begin
         current_state <= ST_Skip_Bus;
         csm_timer <= 9'd0;
      end
      else if (abort) begin
         current_state <= reset;
         csm_timer <= 9'd0;
      end
      else 
      begin
         current_state <= next_state;
         csm_timer <= csm_next_timer;
      end
   end
end // Clocked Block

//-----------------------------------------------------------------
