`resetall
`timescale 1ns/10ps
module sync_bytes_SM( 
   // Port Declarations
   input   wire           clk_usr, 
   input   wire    [9:0]  efifoDout, 
   input   wire           enable, 
   input   wire           loop_en, 
   output  reg            done,       // dbg
   output  reg     [2:0]  sel_cnt
);


// Internal Declarations


// Declare any pre-registered internal signals
reg [2:0] sel_cnt_cld;

// Module Declarations
reg [7:0] Kchar_comma;  //K28.5
reg [7:0] Kchar_eop;  //K28.6
reg [7:0] Kchar_sop;  //K28.1
reg [2:0] max_byte;  // # of wen to send starting from 000 (011: 4 x wen)
reg [2:0] state_o;  

// State encoding
parameter 
          ST_IDLE   = 4'd0,
          ST_SOF    = 4'd1,
          ST_RESET  = 4'd2,
          ST_BYTE_0 = 4'd3,
          ST_EOF    = 4'd4,
          ST_DONE   = 4'd5,
          ST_BYTE_1 = 4'd6,
          ST_BYTE_2 = 4'd7,
          ST_BYTE_3 = 4'd8;

reg [3:0] current_state, next_state;

//-----------------------------------------------------------------
// Next State Block for machine csm
//-----------------------------------------------------------------
always @(
   Kchar_eop, 
   Kchar_sop, 
   current_state, 
   efifoDout, 
   loop_en, 
   max_byte, 
   sel_cnt_cld
)
begin : next_state_block_proc
   case (current_state) 
      ST_IDLE: begin
         next_state = ST_SOF;
      end
      ST_SOF: begin
         if (efifoDout ==Kchar_sop)
            next_state = ST_BYTE_0;
         else
            next_state = ST_SOF;
      end
      ST_RESET: begin
         next_state = ST_IDLE;
      end
      ST_BYTE_0: begin
         next_state = ST_BYTE_1;
      end
      ST_EOF: begin
         next_state = ST_DONE;
      end
      ST_DONE: begin
         if (loop_en == 1)
            next_state = ST_RESET;
         else
            next_state = ST_DONE;
      end
      ST_BYTE_1: begin
         next_state = ST_BYTE_2;
      end
      ST_BYTE_2: begin
         next_state = ST_BYTE_3;
      end
      ST_BYTE_3: begin
         if ((efifoDout ==Kchar_eop)&&(sel_cnt_cld == max_byte))
            next_state = ST_EOF;
         else
            next_state = ST_BYTE_3;
      end
      default: 
         next_state = ST_IDLE;
   endcase
end // Next State Block

//-----------------------------------------------------------------
// Output Block for machine csm
//-----------------------------------------------------------------
always @(
   current_state
)
begin : output_block_proc
   // Default Assignment
   done = 0;
   // Default Assignment To Internals
   Kchar_comma = 8'b10111100;
   Kchar_eop = 8'b11011100;
   Kchar_sop = 8'b00111100;
   max_byte = 3'b100;

   // Combined Actions
   case (current_state) 
      ST_RESET: begin
         done =0;
      end
      ST_DONE: begin
         done =1;
      end
   endcase
end // Output Block

//-----------------------------------------------------------------
// Clocked Block for machine csm
//-----------------------------------------------------------------
always @(
   posedge clk_usr, 
   negedge enable
) 
begin : clocked_block_proc
   if (!enable) begin
      current_state <= ST_IDLE;
   end
   else 
   begin
      current_state <= next_state;

      // Combined Actions
      case (current_state) 
         ST_IDLE: begin
            state_o     = 3'b000;
            sel_cnt_cld     = 0;
         end
         ST_SOF: begin
            state_o  = 3'b001;
         end
         ST_RESET: begin
            state_o     = 3'b000;
            state_o     = 3'b111;
         end
         ST_BYTE_0: begin
            state_o     = 3'b010;
            sel_cnt_cld = sel_cnt_cld +1;
         end
         ST_EOF: begin
            state_o = 3'b110;
         end
         ST_DONE: begin
            state_o = 3'b111;
         end
         ST_BYTE_1: begin
            state_o     = 3'b011;
            sel_cnt_cld = sel_cnt_cld +1;
         end
         ST_BYTE_2: begin
            state_o     = 3'b100;
            sel_cnt_cld = sel_cnt_cld +1;
         end
         ST_BYTE_3: begin
            state_o     = 3'b101;
            sel_cnt_cld = sel_cnt_cld +1;
         end
      endcase
   end
end // Clocked Block

// Concurrent Statements
// Clocked output assignments
always @(
   sel_cnt_cld
)
begin : clocked_output_proc
   sel_cnt = sel_cnt_cld;
end
endmodule // sync_bytes_SM
