// HDL Embedded Text Block 2 trigger_signal
always @(posedge clk)
  begin
    if (!rst)
    begin
    cnt_done <= 0;
    end
    else 
    begin
      case(bus_loop_cnt)
       n_buses:begin
          cnt_done <= 1;
        end
      default:begin
          cnt_done <= 0;
        end
      endcase
  end
end

















